html, body
{
 height: 100%;
 width: 100%;
 overflow: hidden;
}
nav, main
{
 margin-right: 10px;
 margin-left: 10px;
 height: 100%;
 vertical-align: text-top;
 overflow: scroll;
 display: inline-block;
}
.nav-img, .nav-link /\*, .nav-element\*/
{
 vertical-align: baseline;
 display: inline-block;
}
.nav-children
{
 vertical-align: top;
 margin-top: 20px;
 display: block
}

let nav = document.getElementById("main-nav");
let main = document.getElementById("main");
class Nav{
 constructor(main\_element){
 this.elements = [];
 this.main\_element = main\_element;
 this.name = "Error name as not gived";
 }
 add(element){
 this.elements.push(element)
 }
 initialise(){
 let main;
 let elements;
 let to\_follow = [];
 main = ``
 if (this.elements.length == 0){
 to\_follow = [[this.main\_element.id, false]]
 } else {
 to\_follow = [[this.main\_element.id, true]]
 main = `![](develop.svg)`
 }
 main += `[${this.name}](#${this.main_element.id})
 `
 for (const element of this.elements){
 let [main\_, to\_follow\_] = element.initialise();
 main += main\_;
 to\_follow = to\_follow.concat(to\_follow\_);
 }
 main += "";
 return [main, to\_follow];
 }
}
class Follow{
 constructor(id, actual, doc, link, img, child){
 this.id = id;
 this.doc = doc;
 this.link = link;
 this.actual = actual;
 this.img = img;
 this.child = child;
 this.developed = false;
 }
 in(){
 this.link.style.backgroundColor = "red";
 }
 partial(){
 this.link.style.backgroundColor = "blue";
 }
 out(){
 this.link.style.backgroundColor = "unset";
 }
 call(){
 console.log("called")
 if (this.developed){
 console.log("if");
 this.img.src = "develop.svg";
 this.developed = false;
 this.child.style.display = "none";
 } else {
 console.log("else");
 this.developed = true;
 this.img.src = "undevelop.svg"
 this.child.style.display = "inline-block";
 }
 }
}
function Get(nav, main\_element){
 let name = "Le nom n'a pas pu être récupéré;";
 let new\_nav;
 for (section of main\_element.children){
 if (section.id.startsWith(main\_element.id)){
 new\_nav = new Nav(section);
 Get(new\_nav, section);
 nav.add(new\_nav);
 } else if(section.nodeName.toLowerCase() == "name"){
 name = section.innerHTML;
 section.style.display = "none";
 }
 }
 nav.name = name
}
function Install(nav\_element, main){
 let f\_to\_follow = [];
 let f\_main = "";
 for (section of main.children){
 if (section.id.startsWith("section-")){
 let nav = new Nav(section)
 Get(nav, section);
 let [main\_, to\_follow] = nav.initialise();
 f\_main += main\_;
 f\_to\_follow = f\_to\_follow.concat(to\_follow);
 }
 }
 nav\_element.innerHTML = f\_main;
 return f\_to\_follow;
}
let to\_follow = Install(nav, main)
let followed = [];
for (follow of to\_follow){
 let actual = document.getElementById(`nav-element-${follow[0]}`);
 let link = document.getElementById(`nav-link-${follow[0]}`);
 let children = document.getElementById(`nav-chlidren-${follow[0]}`);
 let doc = document.getElementById(`${follow[0]}`);
 let img = false;
 if (follow[1] == true){
 img = document.getElementById(`nav-img-${follow[0]}`);
 }
 let follow\_class = new Follow(follow[0], actual, doc, link, img, children);
 followed.push(follow\_class);
 if (follow[1] == true){
 img.addEventListener("click", function (){
 follow\_class.call();
 });
 }
}
console.log(followed);

window.addEventListener('scroll', () => {
 for (const follow of followed){
 console.log(follow);
 var rect = follow.doc.getBoundingClientRect();
 if (rect.top <= window.innerHeight){
 follow.in();
 } else if (rect.bottom >= 0 && 1 == 0) {
 follow.partial();
 } else {
 follow.out();
 }
 }
});

