Sure, here's a MATLAB function that generates a logo design for Ferris Consulting, incorporating growth, strategy, innovation, and sustainability while subtly nodding to the company's initials:

```matlab
function logoDesign = ferrisLogo()

    % Define the color palette
    colors = rgb(80,150,255); % Light blue
    colors = [colors, 255*ones(1,3)*grey]; % Shades of grey

    % Generate the logo design
    logoDesign = imageresize(hexagonalStar(colors), [120 120], 'bicubic');
    logoDesign = imcrop(logoDesign, [10:110 10:110]);
    logoDesign = imflip(logoDesign, 'y');

    % Add the initials
    initialsPos = [50 50, 55 100, 105 50, 100 10];
    for i = 1:length(initialsPos)
        logoDesign = imoverlay(logoDesign, ferrisInitials(colors(i)), initialsPos(i));
    end
end

function hexagonalStar(colors)
    % Define the parameters of the star
    n = 7; % Number of points in the star
    r = 50; % Radius of the star
    deltaTheta = 2*pi/n; % Increment in theta for each point in the star

    % Generate the points of the star
    points = [];
    for i = 1:n
        theta = (i-1)*deltaTheta;
        x = r*cos(theta);
        y = r*sin(theta);
        points = [points, [x,y]];
    end

    % Plot the star on a blank image
    blankImage = imclear(zeros(1,1), 'uint8');
    for i = 1:length(points)-1
        [x1, y1] = points{i};
        [x2, y2] = points{i+1};
        lineColor = i == 1? colors(1) : colors(2);
        line thickness = 1;
        line(blankImage, x1, y1, x2, y2, lineColor);
    end

    % Return the logo design
    return(blankImage);
end

function ferrisInitials(color)
    % Define the size and position of the initials
    fontName = 'Times New Roman';
    fontSize = 40;
    initials = 'FC';
    xPos = 20;
    yPos = 20;

    % Create a blank image with the specified color
    img = imclear(zeros(1,1), 'uint8');
    set(gca, 'Color', [0 0 0], 'Clipping', 'off');
    hold on;
    color = rgb2ind(color);
    patch('Faces', [1 1], [xPos yPos 0], [xPos+fontSize yPos+fontSize 0],linecolor, 'edgecolor',color, 'filled', 1.0);
    set(gcf, 'units', 'normalized', 'position', [0.5 0.5 0.95 0.95]);

    % Add the initials
    textSize = fontSize/72;
    set(gcf, 'units', 'pixels');
    set(gca, 'fontname', fontName, 'fontsize', fontSize);
    text(initials, xPos, yPos, 'HorizontalAlignment', 'center', 'VerticalAlignment', 'center');

    % Convert the image back to RGB and return it
    img = ind2rgb(img);
    return(img);
end
