function modifyTable(table, string, order) {
    let characterCount = countCharacters(string);

    table.push(characterCount);

    if (order === 'desc') {
        table.sort((a, b) => {
            let aCount = Object.values(a).reduce((a, b) => a + b);
            let bCount = Object.values(b).reduce((a, b) => a + b);

            return bCount - aCount;
        });
